<div class=\"col-md-9\">

<h1> Nos produits </h1>

<div class=\"row\"> 
{% for product in products %}

<div class=\"col-md-4\"> 

<div class=\"product-item text-center\"> 

<a href=\"{{ path('product', {'slug': product.slug})}}\"><img src=\"/uploads/{{product.illustration}}\" alt=\"image produit\" class=\"img-fluid\" width=\"\"></a>
<h4> {{product.name}} </h4>
<span> {{product.subtitle}} </span> <hr>
{{(product.price / 100) | number_format(2) }}euros
</div>   

</div>
{% endfor %}
</div>

</div>
</div>


